package aoc2022;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.*;

import static java.util.stream.Collectors.toMap;
import static java.util.stream.Collectors.toSet;

public class Problem06 {

    public static void main(String[] args) throws IOException {
        solve(4, "mjqjpqmgbljsphdztnvjfqwrcgsmlb");
        solve(4, "bvwbjplbgvbhsrlpgdmjqwftvncz");
        solve(4, "");
        solve(14, "");
    }

    private static void solve(int c, String s) {
        for (int i = 0; i < s.length()-c+1; i++) {
            if (Arrays.stream(s.substring(i, i+c).split("")).collect(toSet()).size() == c ) {
                System.out.println(i+c);
                return;
            }
        }
    }

//     Output
//     7
//     5
//     1804
//     2508

}
